import React, { Component } from "react";
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption,
  Col
} from "reactstrap";

import { Jumbotron, Container } from "reactstrap";

const items = [
  {
    src:
      "data:image/jpeg;base64,",
    altText: "Pomerania Map",
    caption: "Pomeraia Map",
    key: 1
  },
  {
    src:
      "data:image/jpeg;base64, ",
    altText: "Problem Statement",
    caption: "Problem Statement",
    key: 2
  },
  {
    src:
      "data:image/jpeg;base64, ",
    altText: "Interpretable ML",
    caption: "Interpretable ML",
    key: 3
  }
];

class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeIndex: 0,
      animating: false
    };
    this.setAnimating = this.setAnimating.bind(this);
    this.setActiveIndex = this.setActiveIndex.bind(this);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
    this.goToIndex = this.goToIndex.bind(this);
  }
  setActiveIndex(nextIndex) {
    this.setState({ activeIndex: nextIndex });
  }
  setAnimating(animating) {
    this.setState({ animating: animating });
  }
  next = () => {
    if (this.state.animating) {
      return;
    }
    const nextIndex =
      this.state.activeIndex === items.length - 1
        ? 0
        : this.state.activeIndex + 1;
    this.setActiveIndex(nextIndex);
  };

  previous = () => {
    if (this.state.animating) {
      return;
    }
    const nextIndex =
      this.state.activeIndex === 0
        ? items.length - 1
        : this.state.activeIndex - 1;
    this.setActiveIndex(nextIndex);
  };

  goToIndex = newIndex => {
    if (this.state.animating) {
      return;
    }
    this.setActiveIndex(newIndex);
  };

  render() {
    const slides = items.map(item => {
      return (
        <CarouselItem
          onExiting={() => this.setAnimating(true)}
          onExited={() => this.setAnimating(false)}
          key={item.key}
        >
          <Jumbotron fluid>
            <Container fluid>
              <img src={item.src} alt={item.altText} />
            </Container>
          </Jumbotron>
          <CarouselCaption
            captionText={item.caption}
            captionHeader={item.caption}
          />
        </CarouselItem>
      );
    });
    return (
      <Jumbotron fluid>
        <Container fluid>
          <Carousel
            className="display-3"
            activeIndex={this.state.activeIndex}
            next={this.next}
            previous={this.previous}
          >
            <CarouselIndicators
              items={items}
              activeIndex={this.state.activeIndex}
              onClickHandler={this.goToIndex}
            />
            {slides}
            <CarouselControl
              direction="prev"
              directionText="Previous"
              onClickHandler={this.previous}
            />
            <CarouselControl
              direction="next"
              directionText="Next"
              onClickHandler={this.next}
            />
          </Carousel>
        </Container>
      </Jumbotron>
    );
  }
}

export default HomePage;
